    $thismsg .= "\033[32m                         (\033[1mL\033[22m)ist Warriors\n";
    $thismsg .= "\033[32m                         (\033[1mQ\033[22m)uit the game server\n\n";
    $thismsg .= "\033[32m                         Your choice, warrior? [\033[1mE\033[22m]: \033[0m";
    return $thismsg;
}

